// main

body, p, h1, h2, h3, h4, h5, h6, menu, ul, ol, li, dl, dt, dd, table, th, td, form, fieldset, legend, input, textarea, button {
    margin: 0;
    padding: 0;
}

html, body {
    width: 100%;
    height: 100%;
}

img, fieldset {
    border: 0;
}

menu, ul, ol {
    list-style: none;
}

em, address {
    font-style: normal;
}

a, a:link {
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

textarea {
    overflow: auto;
}

option, x:-moz-any-link {
    padding-right: 6px;
}

option, x:-moz-any-link, x:default {
    padding-right: 0;
}

input[type='file']::-webkit-file-upload-button {
    cursor: pointer;
}

button, label {
    cursor: pointer;
}

button::-moz-focus-inner {
    padding: 0 !important;
    border: 0;
}

legend {
    position: absolute;
    top: 0;
    left: 0;
    width: 0;
    height: 0;
    overflow: hidden;
    visibility: hidden;
    font-size: 0;
    line-height: 0;
}

a.skip {
    position: absolute;
    left: 15px;
    top: -30px;
    border: 1px solid #4ec53d;
    color: #fff;
    text-decoration: none;
    padding: 2px 6px 0;
    line-height: 24px;
    font-size: 12px;
    background-color: #000;
    z-index: 100;
    -webkit-transition: .3s;
}

a.skip:active, a.skip:focus {
    top: 0;
}

.blind {
    position: absolute;
    clip: rect(0, 0, 0, 0);
    width: 1px;
    height: 1px;
    margin: -1px;
    overflow: hidden;
}

