.snb_split_area {
    position: absolute;
    top: 0;
    right: -5px;
    z-index: 999;
    width: 7px;
    height: 100%;
    font-size: 0;
    cursor: ew-resize;
}

.lnb_tree li .line_loc:before,
.lnb_tree li .line_loc_bottom:before,
.lnb_tree li .drag_into:before {
    content: '';
    position: absolute;
    left: 12px;
    right: 12px;
    border-width: 0;
    border-style: solid;
    border-color: #157EFB;
}

.lnb_tree li .line_loc:before {
    top: 0;
    border-top-width: 2px;
}

.lnb_tree li .line_loc_bottom:before {
    bottom: 0;
    border-top-width: 2px;
}

.lnb_tree li .drag_into:before {
    top: 0;
    bottom: 0;
    border-width: 2px;
    border-radius: 2px;
}

.lnb_tree li .dragging {
    opacity: 0.4;
}

.lnb_tree li .drop_into,
.lnb_tree li .drop_order {
    position: absolute;
    top: 37%;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 30;
    opacity: 0;
    display: none;
}

.drag_boxmove .lnb_tree li .drop_into, .drag_boxmove
.lnb_tree li .drop_order {
    display: block;
}

.lnb_tree li .drop_order {
    top: 0;
    bottom: 63%;
}

.lnb_tree li.drop_order_bottom {
